def crea_dic():    f = open("frenchssaccent.dic",'r')    dico = list()    for ligne in f:        dico.append(ligne [ 0 : len(ligne)-1 ] )    f.close()    return dicodef mots_possibles(L):    '''entree: liste des lettres tirees SANS JOKER POSSIBLE    sortie: liste des mots que l'on peut ecrire avec'''    dico = crea_dic()    mots = list()    for mot in dico:        l = list(L)        mot_valable = True        for lettre in mot:            if mot_valable:                if (lettre in l): l.remove(lettre)                else: mot_valable = False        if mot_valable:            mots.append(mot)    return mots          def mot_le_plus_long(mots):    '''entree: liste des mots possibles    sortie: le mot le plus long     note: fonctionne avec ou sans le joker'''    n_max = 0    mot_max = 0    for mot in mots:        n = len(mot)        if n > n_max:            n_max = n            mot_max = mot    return mot_max# regles de comptage de pointpoints_1 = ['a', 'e', 'i', 'l', 'n', 'o', 'r', 's', 't' ,'u']points_2 = ['d', 'g', 'm']points_3 = ['b', 'c', 'p']points_4 = ['f', 'h', 'v']points_8 = ['j', 'q']points_10 = ['k', 'w', 'x', 'y', 'z']def score(mot):    ''' entree: un mot     sortie: son score     avec ou sans joker'''    n = 0    for lettre in mot:        if lettre in points_1: n += 1        if lettre in points_2: n += 2        if lettre in points_3: n += 3        if lettre in points_4: n += 4        if lettre in points_8: n += 8        if lettre in points_10: n += 10    return ndef max_score(mots):    ''' entree: une liste de mots    sortie: le mots avec le meilleur score et son score    SANS JOKER'''    max_sc = 0    best_mot = 0    for mot in mots:        sc = score(mot)        if  sc >max_sc :             max_sc = sc            best_mot = mot    return (best_mot, max_sc)## JOKERdef mots_possibles_joker(L):    '''entree: liste des lettres tirees JOKER POSSIBLE    sortie: liste des mots que l'on peut ecrire avec'''    dico = crea_dic()    mots = list()    mots_ecrits = list()    for mot in dico:        l = list(L)        mot_valable = True        mot_ecrit = ''        for lettre in mot:            if mot_valable:                if lettre in l :                     l.remove(lettre)                    mot_ecrit = mot_ecrit + lettre                else:                     if '?' in l :                         l.remove('?')                    else:                         mot_valable = False        if mot_valable:            mots.append(mot)            mots_ecrits.append(mot_ecrit)    return [mots, mots_ecrits]def max_score_joker(mots, mots_ecrits):    ''' entree: une liste de mots    sortie: le mots avec le meilleur score et son score    JOKER'''    max_sc = 0    best_mot = 0    for i in range(len(mots)):        sc = score(mots_ecrits[i])        if  sc >max_sc :             max_sc = sc            best_mot = mots[i]    return (best_mot, max_sc)